import {useState} from "react";

function App() {
  const [animate, setAnimate] = useState(false); // アニメーションを適用するかの状態

  const handleClick = () => {
    setAnimate(true); // アニメーションを開始

    // アニメーションを一定時間後にリセット（必要なら無限ループでなくする）
    setTimeout(() => {
      setAnimate(false);
    }, 5000); // 5秒後にアニメーションをリセット
  };

  return (
    <div className="App">
      <h1
        className={`headline ${animate ? 'scroll-animation' : ''}`} // 動的にクラスを適用
        onClick={handleClick} // クリックイベントをバインド
      >
        TODO
      </h1>
    </div>
  );
}

export default App;
