import {useState} from 'react';


export default function App() {

  const [isSent, setIsSent] = useState(false);
  const [message, setMessage] = useState('Hi!');
  return(
  <form onSubmit={(e) => {
    e.preventDefault();
    setIsSent(true);
    sendMessage(message);
  }}>

    <textarea
      placeholder="Message"
      value={message}
      onChange={e => setMessage(e.target.value)}
    />

    <button type="submit">Send</button>
  </form>
)
}
