import { useState } from 'react';

let nextId=0;
export default function Form() {
 const [name,setName]=useState('');
 const [food,setFood]=useState([]);
 function Reset(){
   setFood([]);
 }
 function Sort(){
   setFood([...food].sort((a,b)=>a.rank-b.rank));
  }
  function Sort2(){
    setFood([...food].sort((a,b)=>b.rank-a.rank));
   }
 return(
  <>
  <h1>料理ランクまとめ</h1>
  <input 
  value={name}
  onChange={e => setName(e.target.value)}
  />


  <button onClick={()=>{
    setFood([...food,{name:name,rank://ランダムな値を生成
      Math.floor(Math.random()*100)}]);
  }}>Add</button>
  <button onClick={Reset}>reset</button>
  <button onClick={Sort}>昇順に並び替え</button>
  <button onClick={Sort2}>降順に並び替え</button>


  <ul>
    {food.map(f=>(
      <>
      <li key={f.name}>名前:{f.name} ランク:{f.rank}</li>
      {/* 削除する内容を追加*/} 
      <button onClick={()=>{
        setFood(food.filter(food=>food.name!==f.name));
      }}>削除</button>
      </>
    ))}
  </ul>
  </>

 )
}
