//お酒が飲めるかどうかを判定していきたいです。
function Listoone(){

}

function Paticipants({name,isOK}){
  return(
    <li>
     {!isOK? (
      <del>{name}さんはお酒が飲めません。</del>
     ):(
      name + "さんはお酒が飲めます。"
     )}
    </li>
  )
}

export default function App() {
  const participants = [
    {name: "田中", isOK: true},
    {name: "佐藤", isOK: false},
    {name: "鈴木", isOK: true},
    {name: "高橋", isOK: false},
    {name: "渡辺", isOK: true},
  ];

  return (
    <div>
      <h1>お酒が飲めるかの有無を判定してます。</h1>
      <h2>飲めない人は次回の飲み会に参加できません。二重線で消しましょう。</h2>
      <ul>
        {participants.map((participant) => (
          <Paticipants name={participant.name} isOK={participant.isOK} />
        ))}
      </ul>
    </div>
  );
}
