//顧客のデータを管理していく。

function Customer({id,name,amount}){
  return(

    <div>
      <h1>id: {id}</h1>
      <h2>name: {name}</h2>
      <h2>amount: {amount}</h2>
    </div>
  )
}

export default function CustomerData(){
  const customers = [
    {id:1,name:"山田",amount:1000},
    {id:2,name:"田中",amount:2000},
    {id:3,name:"佐藤",amount:3000},
    {id:4,name:"鈴木",amount:4000}
  ]
  const hutokyaku= customers.filter(customer=>
    customer.amount>=300
  );

  return(
    <div>
      <h1>顧客一覧</h1>
     {customers.map((customer)=>{
      return <Customer key={customer.id} id={customer.id} name={customer.name} amount={customer.amount
     }/>
     })}
      <h1>振込額が3000以上の顧客</h1>
     {hutokyaku.map((customer)=>{
      return <Customer key={customer.id} id={customer.id} name={customer.name} amount={customer.amount  }/>
     }
      )}
    </div>
  )
}
