//お酒が飲めるかどうかを判定していきたいです。
function Paticipants({name,isOK}){
  if(isOK){
    return(
      <li className="item">{name}さんはお酒が飲めます。⭕️</li>
    )
  }
  return(
    <li className="item">{name}さんはお酒が飲めません。❌</li>
  )

}

export default function App() {
  const participants = [
    {name: "田中", isOK: true},
    {name: "佐藤", isOK: false},
    {name: "鈴木", isOK: true},
    {name: "高橋", isOK: false},
    {name: "渡辺", isOK: true},
  ];

  return (
    <div>
      <h1>お酒が飲めるかの有無を判定してます。</h1>
      <ul>
        {participants.map((participant) => (
          <Paticipants name={participant.name} isOK={participant.isOK} />
        ))}
      </ul>
    </div>
  );
}
