/* MAIN */
function resolveTimeout(timeout, value) {
    return new Promise(resolve => {
        if (timeout === Infinity)
            return;
        setTimeout(() => {
            if (typeof value === 'function') {
                resolve(value());
            }
            else {
                resolve(value);
            }
        }, timeout);
    });
}
;
/* EXPORT */
export default resolveTimeout;
